import React, { useRef, useState } from 'react';

const About = () => {
  const carouselRef = useRef(null);
  const [isDragging, setIsDragging] = useState(false);
  const [startX, setStartX] = useState(0);
  const [scrollLeft, setScrollLeft] = useState(0);

  const blogPosts = [
    {
      title: "2020 (14 Years Old)",
      imgSrc: "../assets/shelf.jpg",
      description:
        "The year of quarantine was the year of me diving into 3d printing. Having stumbled upon a 3d printed boat on TikTok I HAD to learn more. On July 12th I built my Ender 3 Pro. Since then, I've created an entire shelf worth of prints.",
      link: "https://www.3dbenchy.com",
      linkText: "3d printed boat",
    },
    {
      title: "2021-2023 (15-16)",
      imgSrc: "../assets/turtleburg.jpg",
      description:
        "I was tasked to spend 25 hours on a project for school and I thought it would be perfect to make a game. Over 500 hours of graphic designing and coding for the first time later: Turtleburg! My edutainment RPG about the water crisis.",
    },
    {
      title: "2023 (17 Years Old)",
      imgSrc: "../assets/kwk.png",
      description:
        "After Turtleburg, I wanted to dive into other parts of CS. So, I signed up for Kode With Klossy for the summer. I didn't expect to like web development much but ended up coming out with three languages and EdVisor.",
      link: "http://edvisor.cruzcodes.com",
      linkText: '"EdVisor"',
    },
    {
      title: "2023 (17 Years Old)",
      imgSrc: "../assets/cruzcodes.jpg",
      description:
        "Having finished KWK, I was inspired to do web development on my own. I wanted to start simple, my own website. That in which you are scrolling through right now, Thank You!",
      link: "http://cruzcodes.com",
      linkText: "my own website",
    },
    {
      title: "2023 (17 Years Old)",
      imgSrc: "../assets/cheezy.jpg",
      description:
        "I wanted to diversify my summer and did the Girls Who Code SIP for game development. Here I made Cheezy Conundrum and learned a lot about p5.js and JavaScript for game development.",
      link: "https://cruzcodes.itch.io/cheezy-conundrum",
      linkText: "Cheezy Conundrum",
    },
    {
      title: "2023 (17 Years Old)",
      imgSrc: "../assets/gems.PNG",
      description:
        "I was inspired by my female in STEM summer that I founded a Girls STEM club at my school. Here I take on the role of teaching coding for our projects. The first project having been the website itself.",
      link: "http://dbhsgems.com",
      linkText: "website",
    },
    {
      title: "To be Determined",
      imgSrc: "../assets/question.jpg",
      description:
        "This is only the beginning, I have a long way to go before I make a real name for myself in computer science. Next steps: Code/Art!",
    },
  ];

  const handleMouseDown = (e) => {
    setIsDragging(true);
    setStartX(e.pageX - carouselRef.current.offsetLeft);
    setScrollLeft(carouselRef.current.scrollLeft);
  };

  const handleTouchStart = (e) => {
    setIsDragging(true);
    setStartX(e.touches[0].pageX - carouselRef.current.offsetLeft);
    setScrollLeft(carouselRef.current.scrollLeft);
  };

  const handleMouseUp = () => {
    setIsDragging(false);
  };

  const handleMouseMove = (e) => {
    if (!isDragging) return;
    e.preventDefault();
    const x = e.pageX - carouselRef.current.offsetLeft;
    const walk = (x - startX) * 2;
    carouselRef.current.scrollLeft = scrollLeft - walk;
  };

  const handleTouchMove = (e) => {
    if (!isDragging) return;
    const x = e.touches[0].pageX - carouselRef.current.offsetLeft;
    const walk = (x - startX) * 2;
    carouselRef.current.scrollLeft = scrollLeft - walk;
  };

  return (
    <div id="about-area" className="section_padding">
      <div className="container">
        <div className="row">
          <div className="col-md-12">
            <div 
              ref={carouselRef}
              className="about_slider_area"
              onMouseDown={handleMouseDown}
              onMouseUp={handleMouseUp}
              onMouseLeave={handleMouseUp}
              onMouseMove={handleMouseMove}
              onTouchStart={handleTouchStart}
              onTouchEnd={handleMouseUp}
              onTouchMove={handleTouchMove}
            >
              <div className="slider-track">
                {blogPosts.map((post, index) => (
                  <div className="box-area" key={index}>
                    <div className="single-blog wow fadeInUp">
                      <div className="single_blog">
                        <a href="#">
                          <h3 className="post-title">{post.title}</h3>
                        </a>
                        <div className="post-img">
                          <img src={post.imgSrc} alt={post.title} />
                        </div>
                        <p className="blog-text">
                          {post.description}{" "}
                          {post.link && (
                            <a href={post.link} target="_blank" rel="noopener noreferrer">
                              {post.linkText}
                            </a>
                          )}
                        </p>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>
      </div>
      <style jsx>{`
        .about_slider_area {
          overflow-x: auto;
          cursor: grab;
          position: relative;
          padding: 1rem 0;
          scrollbar-width: thin;
          scrollbar-color: #888 #f1f1f1;
        }

        .about_slider_area::-webkit-scrollbar {
          height: 8px;
        }

        .about_slider_area::-webkit-scrollbar-track {
          background: #f1f1f1;
          border-radius: 4px;
        }

        .about_slider_area::-webkit-scrollbar-thumb {
          background: #888;
          border-radius: 4px;
        }

        .slider-track {
          display: grid;
          grid-auto-flow: column;
          grid-auto-columns: calc(50% - 1rem);
          gap: 2rem;
          padding: 0 1rem;
        }

        /* Your original styles */
        .single-blog {
          min-width: 23rem;
          max-width: 25rem;
          transition: all 0.30s ease 0s;
        }

        .single-blog:hover {
          transform: translateY(-10px);
        }

        .single-blog .post-img {
          position: relative;
        }

        .single-blog .post-img img {
          width: 100%;
          object-fit: cover;
          border-radius: 1rem;
        }

        .single-blog .post-img:after {
          content: "";
          position: absolute;
          width: 100%;
          height: 100%;
          top: 0;
          left: 0;
          opacity: 0;
          border-radius: 1rem;
          background: rgba(81, 157, 187, 0.6);
          transition: all 0.30s ease 0s;
        }

        .single-blog:hover .post-img:after {
          opacity: 1;
        }

        .single-blog .single_blog {
          border-top: none;
          padding: 25px 20px 25px;
          position: relative;
        }

        .post-title {
          text-align: center;
          display: flex;
          justify-content: center;
          font-size: 20px;
          text-transform: capitalize;
          transition: all 0.3s ease 0s;
        }

        .single-blog .blog-text {
          text-align: center;
          font-size: 15px;
          line-height: 23px;
          margin: 7px 0 10px;
        }

        @media (max-width: 768px) {
          .slider-track {
            grid-auto-columns: 85%;
          }
        }
      `}</style>
    </div>
  );
};

export default About;
