import React, { useRef } from 'react';

const Slider = ({ items }) => {
  const scrollContainerRef = useRef(null);

  const scroll = (direction) => {
    const container = scrollContainerRef.current;
    if (!container) return;

    const scrollAmount = container.clientWidth / 2;
    container.scrollBy({
      left: direction === 'left' ? -scrollAmount : scrollAmount,
      behavior: 'smooth'
    });
  };

  return (
    <div id="about-area" className="section_padding relative">
      <button 
        onClick={() => scroll('left')} 
        className="absolute left-4 top-1/2 transform -translate-y-1/2 z-10 text-white text-2xl bg-transparent border-none cursor-pointer"
        style={{ textShadow: '2px 2px 4px rgba(0,0,0,0.5)' }}
      >
        ←
      </button>

      <button 
        onClick={() => scroll('right')} 
        className="absolute right-4 top-1/2 transform -translate-y-1/2 z-10 text-white text-2xl bg-transparent border-none cursor-pointer"
        style={{ textShadow: '2px 2px 4px rgba(0,0,0,0.5)' }}
      >
        →
      </button>

      <div 
        ref={scrollContainerRef}
        className="flex overflow-x-auto gap-8 scroll-smooth"
        style={{ 
          scrollbarWidth: 'none',
          msOverflowStyle: 'none',
          WebkitOverflowScrolling: 'touch',
          padding: '0 20px'
        }}
      >
        {items.map((item, index) => (
          <div key={index} className="flex-none w-96">
            <div className="single-blog wow fadeInUp">
              <div className="single_blog">
                <a href="#">
                  <h3 className="post-title">{item.year}</h3>
                </a>
                <div className="post-img">
                  <img src={item.image} alt="" />
                </div>
                <p className="blog-text">
                  {item.text}
                  {item.link && (
                    <a
                      href={item.link}
                      target="_blank"
                      rel="noopener noreferrer"
                    >
                      {item.linkText}
                    </a>
                  )}
                </p>
              </div>
            </div>
          </div>
        ))}
      </div>

      <style jsx>{`
        ::-webkit-scrollbar {
          display: none;
        }
      `}</style>
    </div>
  );
};

const TimelineContent = () => {
  const timelineData = [
    {
      year: "2020 (14 Years Old)",
      image: "assets/shelf.jpg",
      text: "The year of quarantine was the year of me diving into 3d printing. Having stumbled upon a 3d printed boat on TikTok I HAD to learn more. On July 12th I built my Ender 3 Pro. Since then, I've created an entire shelf worth of prints.",
      link: "https://www.3dbenchy.com",
      linkText: "3d printed boat"
    },
    {
      year: "2021-2023 (15-16)",
      image: "assets/turtleburg.jpg",
      text: "I was tasked to spend 25 hours on a project for school and I thought it would be perfect to make a game. Over 500 hours of graphic designing and coding for the first time later: Turtleburg! My edutainment RPG about the water crisis."
    },
    {
      year: "2023 (17 Years Old)",
      image: "assets/kwk.png",
      text: "After Turtleburg, I wanted to dive into other parts of CS. So, I signed up for Kode With Klossy for the summer. I didn't expect to like web development much but ended up coming out with three languages and EdVisor.",
      link: "edvisor.cruzcodes.com",
      linkText: "EdVisor"
    },
    {
      year: "2023 (17 Years Old)",
      image: "assets/cruzcodes.jpg",
      text: "Having finished KWK, I was inspired to do web development on my own. I wanted to start simple, my own website. That in which you are scrolling through right now, Thank You!",
      link: "cruzcodes.com",
      linkText: "my own website"
    },
    {
      year: "2023 (17 Years Old)",
      image: "assets/cheezy.jpg",
      text: "I wanted to diversify my summer and did the Girls Who Code SIP for game development. Here I made Cheezy Conundrum and learned alot about p5.js and JavaScript for game development.",
      link: "http://cheezy.cruzcodes.com",
      linkText: "Cheezy Conundrum"
    },
    {
      year: "2023 (17 Years Old)",
      image: "assets/gems.PNG",
      text: "I was inspired by my female in STEM summer that I founded a Girls STEM club at my school. Here I take on the role of teaching coding for our projects. The first project having been the website itself.",
      link: "http://dbhsgems.com",
      linkText: "website"
    }
  ];

  return <Slider items={timelineData} />;
};

export { TimelineContent };

// Example usage for Tools
const ToolsContent = () => {
  const toolsData = [
    { icon: "fa-brands fa-html5", name: "HTML5" },
    { icon: "fa-brands fa-css3-alt", name: "CSS3" },
    { icon: "fa-brands fa-square-js", name: "JavaScript" },
    { icon: "fa-brands fa-react", name: "React" },
    { icon: "fa-brands fa-java", name: "Java" },
    { icon: "", name: "p5.js", image: "assets/p5js.png" },
    { icon: "fa-brands fa-python", name: "Python" },
    { icon: "", name: "Godot Engine", image: "assets/godot.png" },
    { icon: "fa-brands fa-square-git", name: "Git" }
  ];

  const renderToolItem = (item) => (
    <div className="flex flex-col items-center justify-center p-4">
      {item.icon ? (
        <i className={`${item.icon} text-6xl mb-2`} />
      ) : (
        <img src={item.image} alt={item.name} className="w-24 h-24 mb-2 object-contain" />
      )}
      <span className="text-lg font-medium">{item.name}</span>
    </div>
  );

  return (
    <Slider
      items={toolsData}
      renderItem={renderToolItem}
      title="Skills"
      subtitle="I love to make things both for the general web and for edutainment purposes. Here are the tools I use:"
    />
  );
};

export { Slider, ToolsContent };
