import React from 'react';

const Slide = ({ content, type }) => {
  if (type === 'about') {
    return (
      <div className="w-96 h-[28rem] p-6 bg-slate-800 rounded-lg shadow-lg">
        <h3 className="text-2xl font-bold text-white mb-4">{content.title}</h3>
        <div className="h-48 w-full mb-4">
          <img 
            src={content.imageSrc || "/api/placeholder/400/225"}
            alt={content.title}
            className="w-full h-full object-cover rounded"
          />
        </div>
        <p className="text-gray-300 text-base overflow-y-auto max-h-48">
          {content.description}
        </p>
      </div>
    );
  }

  if (type === 'skills') {
    return (
      <div className="w-96 h-[28rem] flex flex-col items-center justify-center p-4 bg-slate-800 rounded-lg shadow-lg">
        <div className="w-16 h-16 mb-2 flex items-center justify-center">
          {content.icon}
        </div>
        <span className="text-white text-lg font-medium">{content.name}</span>
      </div>
    );
  }

  return null;
};

export default Slide;
