import React from 'react';

const Slide = ({ content, type }) => {
  if (type === 'about') {
    return (
      <div className="w-full max-w-lg mx-auto p-6 bg-slate-800 rounded-lg shadow-lg">
        <h3 className="text-xl font-bold text-white mb-4">{content.title}</h3>
        <div className="aspect-video relative mb-4">
          <img 
            src={content.imageSrc}
            alt={content.title}
            className="w-full h-full object-cover rounded"
          />
        </div>
        <p className="text-gray-300">{content.description}</p>
      </div>
    );
  }

  if (type === 'skills') {
    return (
      <div className="flex flex-col items-center p-4">
        <div className="w-16 h-16 mb-2">
          {content.icon}
        </div>
        <span className="text-white text-lg">{content.name}</span>
      </div>
    );
  }
};

export default Slide;
