import React, { useState } from 'react';
import { ChevronLeft, ChevronRight } from 'lucide-react';
import Slide from './Slide';

const Slider = ({ items, type, title, description }) => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const itemsPerPage = type === 'about' ? 1 : 3;
  const totalPages = Math.ceil(items.length / itemsPerPage);

  const nextSlide = () => {
    setCurrentIndex((prev) => (prev + 1) % totalPages);
  };

  const prevSlide = () => {
    setCurrentIndex((prev) => (prev - 1 + totalPages) % totalPages);
  };

  return (
    <div className="w-full max-w-6xl mx-auto p-8">
      <h1 className="text-4xl font-bold text-white mb-4 text-center">{title}</h1>
      {description && (
        <p className="text-gray-300 text-center mb-8">{description}</p>
      )}

      <div className="relative">
        <button 
          onClick={prevSlide}
          className="absolute left-0 top-1/2 -translate-y-1/2 -translate-x-12 bg-slate-700 p-2 rounded-full"
        >
          <ChevronLeft className="text-white" />
        </button>

        <div className="flex justify-center items-center gap-8 overflow-hidden">
          {items
            .slice(
              currentIndex * itemsPerPage,
              currentIndex * itemsPerPage + itemsPerPage
            )
            .map((item, idx) => (
              <Slide key={idx} content={item} type={type} />
            ))}
        </div>

        <button 
          onClick={nextSlide}
          className="absolute right-0 top-1/2 -translate-y-1/2 translate-x-12 bg-slate-700 p-2 rounded-full"
        >
          <ChevronRight className="text-white" />
        </button>
      </div>

      <div className="flex justify-center gap-2 mt-6">
        {Array.from({ length: totalPages }).map((_, idx) => (
          <button
            key={idx}
            onClick={() => setCurrentIndex(idx)}
            className={`w-3 h-3 rounded-full ${
              idx === currentIndex ? 'bg-blue-500' : 'bg-gray-600'
            }`}
          />
        ))}
      </div>
    </div>
  );
};

export default Slider;
