import React, { useState, useEffect } from "react";

const Frontpage = () => {
  const [loopNum, setLoopNum] = useState(0);
  const [isDeleting, setIsDeleting] = useState(false);
  const [text, setText] = useState("");
  const [delta, setDelta] = useState(150); // Faster typing speed
  const [index, setIndex] = useState(1);
  const toRotate = ["Web Developer", "Web Designer", "UI/UX Designer"];
  const period = 2000; // Reduced pause duration

  useEffect(() => {
    let ticker = setInterval(() => {
      tick();
    }, delta);

    return () => {
      clearInterval(ticker);
    };
  }, [text]);

  const tick = () => {
    let i = loopNum % toRotate.length;
    let fullText = toRotate[i];
    let updatedText = isDeleting
      ? fullText.substring(0, text.length - 1)
      : fullText.substring(0, text.length + 1);

    setText(updatedText);

    if (isDeleting) {
      setDelta(100); 
    }

    if (!isDeleting && updatedText === fullText) {
      setIsDeleting(true);
      setDelta(period); 
    } else if (isDeleting && updatedText === "") {
      setIsDeleting(false);
      setLoopNum(loopNum + 1);
      setDelta(150); 
    }
  };

  return (
    <section className="frontpage">
      <div className="start">
        <h4 className="hi">
          Hey Friend!
          <h1>
            {`My name is Sam`}{" "}
            <span
              className="txt-rotate"
              dataPeriod="1000"
              data-rotate='[ "Web Developer", "Web Designer", "UI/UX Designer" ]'
            >
              <span className="wrap">
                {text}
                <br />
              </span>
            </span>
          </h1>
        </h4>
      </div>
      <div className="sam">
        <a
          href="https://www.linkedin.com/in/samanthajcruz/"
          target="_blank"
          rel="noreferrer"
        >
          <i className="fa-brands fa-linkedin" id="mob1"></i>
        </a>
        <img className="me" src="assets/me.jpg" alt="Sam" />
        <a
          href="https://github.com/samcruzcodes"
          target="_blank"
          rel="noreferrer"
        >
          <i className="fa-brands fa-square-github" id="mob2"></i>
        </a>
        <div className="medias">
          <a
            href="https://www.linkedin.com/in/samanthajcruz/"
            target="_blank"
            rel="noreferrer"
          >
            <i className="fa-brands fa-linkedin"></i>
          </a>
          <a
            href="https://github.com/samcruzcodes"
            target="_blank"
            rel="noreferrer"
          >
            <i className="fa-brands fa-square-github"></i>
          </a>
        </div>
      </div>
    </section>
  );
};

export default Frontpage;
