import React, { useState, useRef, useEffect } from 'react';
import { ChevronLeft, ChevronRight } from 'lucide-react';

const Slider = ({ items, renderItem, title, subtitle }) => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [startX, setStartX] = useState(null);
  const [isDragging, setIsDragging] = useState(false);
  const sliderRef = useRef(null);

  const handleTouchStart = (e) => {
    setStartX(e.touches[0].clientX);
    setIsDragging(true);
  };

  const handleMouseDown = (e) => {
    setStartX(e.clientX);
    setIsDragging(true);
  };

  const handleTouchMove = (e) => {
    if (!isDragging) return;
    const currentX = e.touches[0].clientX;
    handleSlideGesture(currentX);
  };

  const handleMouseMove = (e) => {
    if (!isDragging) return;
    const currentX = e.clientX;
    handleSlideGesture(currentX);
  };

  const handleSlideGesture = (currentX) => {
    if (startX === null) return;

    const diff = startX - currentX;
    const threshold = 50;

    if (Math.abs(diff) > threshold) {
      if (diff > 0) {
        nextSlide();
      } else {
        prevSlide();
      }
      setStartX(null);
      setIsDragging(false);
    }
  };

  const handleTouchEnd = () => {
    setStartX(null);
    setIsDragging(false);
  };

  const handleMouseUp = () => {
    setStartX(null);
    setIsDragging(false);
  };

  const nextSlide = () => {
    setCurrentSlide((prev) => (prev === items.length - 1 ? 0 : prev + 1));
  };

  const prevSlide = () => {
    setCurrentSlide((prev) => (prev === 0 ? items.length - 1 : prev - 1));
  };

  useEffect(() => {
    const handleKeyDown = (e) => {
      if (e.key === 'ArrowLeft') prevSlide();
      if (e.key === 'ArrowRight') nextSlide();
    };

    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, []);

  return (
    <section className="w-full max-w-4xl mx-auto px-4 py-8">
      {title && <h1 className="text-3xl font-bold text-center mb-8">{title}</h1>}
      {subtitle && <p className="text-center mb-12">{subtitle}</p>}

      <div 
        ref={sliderRef}
        className="relative bg-white rounded-lg shadow-lg p-6 select-none"
        onTouchStart={handleTouchStart}
        onTouchMove={handleTouchMove}
        onTouchEnd={handleTouchEnd}
        onMouseDown={handleMouseDown}
        onMouseMove={handleMouseMove}
        onMouseUp={handleMouseUp}
        onMouseLeave={handleMouseUp}
      >
        <button
          onClick={prevSlide}
          className="absolute left-2 top-1/2 transform -translate-y-1/2 bg-white rounded-full p-2 shadow-lg hover:bg-gray-100 z-10"
        >
          <ChevronLeft className="w-6 h-6" />
        </button>
        <button
          onClick={nextSlide}
          className="absolute right-2 top-1/2 transform -translate-y-1/2 bg-white rounded-full p-2 shadow-lg hover:bg-gray-100 z-10"
        >
          <ChevronRight className="w-6 h-6" />
        </button>

        <div className="overflow-hidden relative">
          <div 
            className="flex transition-transform duration-300 ease-in-out"
            style={{ 
              transform: `translateX(-${currentSlide * 100}%)`,
              width: `${items.length * 100}%`
            }}
          >
            {items.map((item, index) => (
              <div 
                key={index}
                className="w-full flex-shrink-0"
                style={{ width: `${100 / items.length}%` }}
              >
                {renderItem(item, index)}
              </div>
            ))}
          </div>
        </div>

        <div className="flex justify-center gap-2 mt-6">
          {items.map((_, index) => (
            <button
              key={index}
              onClick={() => setCurrentSlide(index)}
              className={`w-2 h-2 rounded-full ${
                currentSlide === index ? 'bg-blue-600' : 'bg-gray-300'
              }`}
            />
          ))}
        </div>
      </div>
    </section>
  );
};

// Example usage for Timeline
const TimelineContent = () => {
  const timelineData = [
    {
      year: "2020 (14 Years Old)",
      image: "/api/placeholder/400/300",
      text: "The year of quarantine was the year of me diving into 3d printing. Having stumbled upon a 3d printed boat on TikTok I HAD to learn more. On July 12th I built my Ender 3 Pro. Since then, I've created an entire shelf worth of prints.",
      link: "https://www.3dbenchy.com",
      linkText: "3d printed boat"
    },
    {
      year: "2021-2023 (15-16)",
      image: "/api/placeholder/400/300",
      text: "I was tasked to spend 25 hours on a project for school and I thought it would be perfect to make a game. Over 500 hours of graphic designing and coding for the first time later: Turtleburg! My edutainment RPG about the water crisis.",
    },
    {
      year: "2023 (17 Years Old)",
      image: "/api/placeholder/400/300",
      text: "After Turtleburg, I wanted to dive into other parts of CS. So, I signed up for Kode With Klossy for the summer. I didn't expect to like web development much but ended up coming out with three languages and EdVisor.",
      link: "edvisor.cruzcodes.com",
      linkText: "EdVisor"
    },
  ];

  const renderTimelineItem = (item) => (
    <div className="flex flex-col items-center px-4">
      <h3 className="text-xl font-semibold mb-4">{item.year}</h3>
      <div className="w-full h-64 mb-6 relative overflow-hidden rounded-lg">
        <img
          src={item.image}
          alt={`Timeline event`}
          className="w-full h-full object-cover"
        />
      </div>
      <p className="text-gray-700 text-center max-w-2xl">
        {item.text}
        {item.link && (
          <a
            href={item.link}
            target="_blank"
            rel="noopener noreferrer"
            className="text-blue-600 hover:text-blue-800 ml-1"
          >
            {item.linkText}
          </a>
        )}
      </p>
    </div>
  );

  return (
    <Slider
      items={timelineData}
      renderItem={renderTimelineItem}
    />
  );
};

// Example usage for Tools
const ToolsContent = () => {
  const toolsData = [
    { icon: "fa-brands fa-html5", name: "HTML5" },
    { icon: "fa-brands fa-css3-alt", name: "CSS3" },
    { icon: "fa-brands fa-square-js", name: "JavaScript" },
    { icon: "fa-brands fa-react", name: "React" },
    { icon: "fa-brands fa-java", name: "Java" },
    { icon: "", name: "p5.js", image: "assets/p5js.png" },
    { icon: "fa-brands fa-python", name: "Python" },
    { icon: "", name: "Godot Engine", image: "assets/godot.png" },
    { icon: "fa-brands fa-square-git", name: "Git" }
  ];

  const renderToolItem = (item) => (
    <div className="flex flex-col items-center justify-center p-4">
      {item.icon ? (
        <i className={`${item.icon} text-6xl mb-2`} />
      ) : (
        <img src={item.image} alt={item.name} className="w-24 h-24 mb-2 object-contain" />
      )}
      <span className="text-lg font-medium">{item.name}</span>
    </div>
  );

  return (
    <Slider
      items={toolsData}
      renderItem={renderToolItem}
      title="Skills"
      subtitle="I love to make things both for the general web and for edutainment purposes. Here are the tools I use:"
    />
  );
};

export { Slider, TimelineContent, ToolsContent };
