import React, { useRef, useState, useEffect } from 'react';
import { ChevronLeft, ChevronRight } from 'lucide-react';

const Slider = ({ 
  items, 
  renderItem,
  title,
  subtitle
}) => {
  const scrollContainerRef = useRef(null);
  const [showControls, setShowControls] = useState(true);
  const [canScrollLeft, setCanScrollLeft] = useState(false);
  const [canScrollRight, setCanScrollRight] = useState(true);

  const checkScroll = () => {
    const container = scrollContainerRef.current;
    if (!container) return;

    setCanScrollLeft(container.scrollLeft > 0);
    setCanScrollRight(
      container.scrollLeft < container.scrollWidth - container.clientWidth
    );
  };

  useEffect(() => {
    const container = scrollContainerRef.current;
    if (!container) return;

    checkScroll();
    container.addEventListener('scroll', checkScroll);
    window.addEventListener('resize', checkScroll);

    return () => {
      container.removeEventListener('scroll', checkScroll);
      window.removeEventListener('resize', checkScroll);
    };
  }, []);

  const scroll = (direction) => {
    const container = scrollContainerRef.current;
    if (!container) return;

    const scrollAmount = container.clientWidth * 0.8;
    container.scrollBy({
      left: direction === 'left' ? -scrollAmount : scrollAmount,
      behavior: 'smooth'
    });
  };

  const defaultRenderItem = (item, index) => (
    <div key={index} className="flex-none w-96 px-4">
      <div className="rounded-lg overflow-hidden shadow-lg bg-white dark:bg-gray-800">
        <div className="p-6">
          <h3 className="text-xl font-bold mb-4">{item.year}</h3>
          {item.image && (
            <div className="relative h-48 mb-4">
              <img 
                src={item.image} 
                alt="" 
                className="w-full h-full object-cover rounded-md"
              />
            </div>
          )}
          <p className="text-gray-600 dark:text-gray-300">
            {item.text}
            {item.link && (
              <a
                href={item.link}
                target="_blank"
                rel="noopener noreferrer"
                className="ml-1 text-blue-500 hover:text-blue-600"
              >
                {item.linkText}
              </a>
            )}
          </p>
        </div>
      </div>
    </div>
  );

  return (
    <div className="relative py-8">
      {title && (
        <div className="text-center mb-8">
          <h2 className="text-3xl font-bold mb-2">{title}</h2>
          {subtitle && <p className="text-gray-600 dark:text-gray-300">{subtitle}</p>}
        </div>
      )}

      {showControls && (
        <>
          <button 
            onClick={() => scroll('left')}
            disabled={!canScrollLeft}
            className="absolute left-2 top-1/2 -translate-y-1/2 z-10 p-2 rounded-full bg-white/80 dark:bg-gray-800/80 shadow-lg backdrop-blur-sm disabled:opacity-30 disabled:cursor-not-allowed transition-opacity"
            aria-label="Scroll left"
          >
            <ChevronLeft className="w-6 h-6" />
          </button>

          <button 
            onClick={() => scroll('right')}
            disabled={!canScrollRight}
            className="absolute right-2 top-1/2 -translate-y-1/2 z-10 p-2 rounded-full bg-white/80 dark:bg-gray-800/80 shadow-lg backdrop-blur-sm disabled:opacity-30 disabled:cursor-not-allowed transition-opacity"
            aria-label="Scroll right"
          >
            <ChevronRight className="w-6 h-6" />
          </button>
        </>
      )}

      <div 
        ref={scrollContainerRef}
        className="flex overflow-x-auto gap-4 scroll-smooth scrollbar-hide snap-x snap-mandatory"
      >
        {items.map((item, index) => (
          <div key={index} className="snap-start">
            {renderItem ? renderItem(item, index) : defaultRenderItem(item, index)}
          </div>
        ))}
      </div>
    </div>
  );
};

const TimelineContent = () => {
  const timelineData = [
    {
      year: "2020 (14 Years Old)",
      image: "assets/shelf.jpg",
      text: "The year of quarantine was the year of me diving into 3d printing. Having stumbled upon a 3d printed boat on TikTok I HAD to learn more. On July 12th I built my Ender 3 Pro. Since then, I've created an entire shelf worth of prints.",
      link: "https://www.3dbenchy.com",
      linkText: "3d printed boat"
    },
    // ... rest of the timeline data
  ];

  return <Slider items={timelineData} />;
};

const ToolsContent = () => {
  const toolsData = [
    { icon: "fa-brands fa-html5", name: "HTML5" },
    { icon: "fa-brands fa-css3-alt", name: "CSS3" },
    { icon: "fa-brands fa-square-js", name: "JavaScript" },
    { icon: "fa-brands fa-react", name: "React" },
    { icon: "fa-brands fa-java", name: "Java" },
    { icon: "", name: "p5.js", image: "assets/p5js.png" },
    { icon: "fa-brands fa-python", name: "Python" },
    { icon: "", name: "Godot Engine", image: "assets/godot.png" },
    { icon: "fa-brands fa-square-git", name: "Git" }
  ];

  const renderToolItem = (item) => (
    <div className="flex-none w-40 p-4">
      <div className="flex flex-col items-center justify-center">
        {item.icon ? (
          <i className={`${item.icon} text-5xl mb-3`} />
        ) : (
          <img src={item.image} alt={item.name} className="w-16 h-16 mb-3 object-contain" />
        )}
        <span className="text-lg font-medium text-center">{item.name}</span>
      </div>
    </div>
  );

  return (
    <Slider
      items={toolsData}
      renderItem={renderToolItem}
      title="Skills"
      subtitle="I love to make things both for the general web and for edutainment purposes. Here are the tools I use:"
    />
  );
};

export { Slider, TimelineContent, ToolsContent };
