import React from "react";
import ProjectCard from "./ProjectCard";

const ProjectsSection = ({ category, projects }) => {
  return (
    <section id="projects">
      <style jsx="true">{`
        #projects {
          padding: 4rem 0;
        }
        .center {
          max-width: 1200px;
          margin: 0 auto;
        }
        .projects {
          display: flex;
          flex-direction: column;
          gap: 3rem;
        }
        .proje {
          display: flex;
          justify-content: space-between;
          align-items: center;
        }
        .proje:nth-child(even) {
          flex-direction: row-reverse;
        }
        #project {
          width: 200px;
          height: 200px;
          object-fit: cover;
        }
        .textt {
          flex: 1;
          padding: 1rem;
        }
        .btn-container {
          display: flex;
          gap: 1rem;
        }
        .btn1 a {
          text-decoration: none;
          padding: 0.5rem 1rem;
          background-color: #2d8cbb;
          color: white;
          border-radius: 5px;
        }
        .btn1 a:hover {
          background-color: #1f6582;
        }
        .btn1 {
          display: inline;
          margin-left: 2.8rem;
          position: relative;
          border-bottom: 2px solid #395068;
        }
        .btn1:hover {
          color: #fff;
        }
        .btn1::before {
          content: "";
          position: absolute;
          left: 0;
          top: 0;
          width: 100%;
          height: 100%;
          background: #395068;
          z-index: -1;
          transition: transform 0.5s;
          transform-origin: 0 0;
          transition-timing-function: cubic-bezier(0.5, 1.6, 0.4, 0.7);
        }
        .btn1::before {
          transform: scaleX(0);
        }
        .btn1:hover::before {
          transform: scaleX(1);
        }
        #flipped #project {
          margin: 1rem;
          max-width: 26rem;
          border: 0.3rem solid #395068;
          transition: background-color 0.3s ease-in-out, transform 0.3s ease-in-out;
        }
        #flipped {
          display: flex;
          margin-top: -5rem;
        }
        #flipped .textt {
          display: block;
          max-width: 26rem;
          font-size: 0.8rem;
        }
        h1 {
          font-size: 2rem;
          text-align: center;
        }
      `}</style>

      <div className="center">
        <h1 style={{ marginBottom: "3rem" }}>
          <u>{category}</u>
        </h1>
        <div className="projects">
          {projects.map((project, index) => (
            <ProjectCard
              key={index}
              title={project.title}
              description={project.description}
              imageUrl={project.imageUrl}
              githubLink={project.githubLink}
              viewLink={project.viewLink}
            />
          ))}
        </div>
      </div>
    </section>
  );
};

export default ProjectsSection;
