import React, { useState } from 'react';
import { ChevronLeft, ChevronRight } from 'lucide-react';
import Slide from './slide';

const Slider = ({ items }) => {
  const [currentIndex, setCurrentIndex] = useState(0);

  const nextSlide = () => {
    setCurrentIndex((prev) => (prev + 1) % items.length);
  };

  const prevSlide = () => {
    setCurrentIndex((prev) => (prev - 1 + items.length) % items.length);
  };

  return (
    <div className="w-full bg-slate-900 min-h-screen py-12">
      <div className="max-w-6xl mx-auto px-4">
        <div className="relative">
          <button
            onClick={prevSlide}
            className="absolute left-4 top-1/2 -translate-y-1/2 bg-slate-700 p-2 rounded-full z-10 hover:bg-slate-600 transition-colors"
          >
            <ChevronLeft className="text-white w-6 h-6" />
          </button>

          <div className="overflow-hidden px-4">
            <div className="transition-transform duration-500 ease-in-out">
              <Slide content={items[currentIndex]} type="about" />
            </div>
          </div>

          <button
            onClick={nextSlide}
            className="absolute right-4 top-1/2 -translate-y-1/2 bg-slate-700 p-2 rounded-full z-10 hover:bg-slate-600 transition-colors"
          >
            <ChevronRight className="text-white w-6 h-6" />
          </button>
        </div>

        <div className="flex justify-center gap-2 mt-8">
          {items.map((_, idx) => (
            <button
              key={idx}
              onClick={() => setCurrentIndex(idx)}
              className={`w-3 h-3 rounded-full transition-colors ${
                idx === currentIndex ? 'bg-blue-500' : 'bg-gray-600'
              }`}
            />
          ))}
        </div>
      </div>
    </div>
  );
};

export default Slider;
