import React from "react";

const ProjectCard = ({ title, description, imageUrl, githubLink, viewLink, isFlipped = false }) => {
  return (
    <div className={`proje ${isFlipped ? 'flipped' : ''}`}>
      <style jsx="true">{`
        .proje {
          display: flex;
          justify-content: space-between;
          align-items: center;
          margin-bottom: 2rem;
        }
        .proje.flipped {
          flex-direction: row-reverse;
        }
        #project {
          width: 200px;
          height: 200px;
          object-fit: cover;
          border: 0.3rem solid #395068;
          transition: background-color 0.3s ease-in-out, transform 0.3s ease-in-out;
        }
        .textt {
          flex: 1;
          padding: 1rem;
          font-size: 0.9rem;
        }
        .btn-container {
          display: flex;
          gap: 1rem;
        }
        .btn1 a {
          text-decoration: none;
          padding: 0.5rem 1rem;
          background-color: #2d8cbb;
          color: white;
          border-radius: 5px;
        }
        .btn1 a:hover {
          background-color: #1f6582;
        }
        .btn1 {
          display: inline;
          margin-left: 2.8rem;
          position: relative;
          border-bottom: 2px solid #395068;
        }
        .btn1:hover {
          color: #fff;
        }
        .btn1::before {
          content: "";
          position: absolute;
          left: 0;
          top: 0;
          width: 100%;
          height: 100%;
          background: #395068;
          z-index: -1;
          transition: transform 0.5s;
          transform-origin: 0 0;
          transition-timing-function: cubic-bezier(0.5, 1.6, 0.4, 0.7);
        }
        .btn1::before {
          transform: scaleX(0);
        }
        .btn1:hover::before {
          transform: scaleX(1);
        }
      `}</style>

      <img id="project" src={imageUrl} alt={title} />
      <div className="textt">
        <p>{description}</p>
        <div className="btn-container">
          {viewLink && (
            <div className="btn1">
              <a href={viewLink} target="_blank" rel="noreferrer">
                &nbsp;&nbsp;View&nbsp;
              </a>
            </div>
          )}
          {githubLink && (
            <div className="btn1">
              <a href={githubLink} target="_blank" rel="noreferrer">
                &nbsp;GitHub&nbsp;
              </a>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default ProjectCard;
