import React from 'react';

const Slide = ({ content, type }) => {
  if (type === 'about') {
    return (
      <div className="w-full max-w-4xl mx-auto flex flex-col items-center">
        <div className="text-center mb-4">
          <h3 className="text-xl font-bold text-white mb-2">{content.year}</h3>
          <span className="text-gray-400">{content.age}</span>
        </div>
        <div className="w-full bg-slate-800 rounded-xl overflow-hidden shadow-lg">
          <img
            src={content.imageSrc || "/api/placeholder/800/400"}
            alt={content.title}
            className="w-full h-64 object-cover"
          />
          <div className="p-6">
            <p className="text-gray-300 text-center text-lg leading-relaxed">
              {content.description}
            </p>
          </div>
        </div>
      </div>
    );
  }

  return null;
};

export default Slide;
