import React, { useRef, useState, useEffect } from 'react';
import { ChevronLeft, ChevronRight } from 'lucide-react';

const Slider = ({ 
  items, 
  renderItem,
  title,
  subtitle
}) => {
  const scrollContainerRef = useRef(null);
  const [showControls, setShowControls] = useState(true);
  const [canScrollLeft, setCanScrollLeft] = useState(false);
  const [canScrollRight, setCanScrollRight] = useState(true);

  const checkScroll = () => {
    const container = scrollContainerRef.current;
    if (!container) return;

    setCanScrollLeft(container.scrollLeft > 0);
    setCanScrollRight(
      container.scrollLeft < container.scrollWidth - container.clientWidth
    );
  };

  useEffect(() => {
    const container = scrollContainerRef.current;
    if (!container) return;

    checkScroll();
    container.addEventListener('scroll', checkScroll);
    window.addEventListener('resize', checkScroll);

    return () => {
      container.removeEventListener('scroll', checkScroll);
      window.removeEventListener('resize', checkScroll);
    };
  }, []);

  const scroll = (direction) => {
    const container = scrollContainerRef.current;
    if (!container) return;

    const scrollAmount = container.clientWidth * 0.8;
    container.scrollBy({
      left: direction === 'left' ? -scrollAmount : scrollAmount,
      behavior: 'smooth'
    });
  };

  const defaultRenderItem = (item, index) => (
    <div key={index} className="flex-none w-[800px] px-8">
      <div className="flex flex-col items-center text-center">
        <h3 className="text-4xl font-bold mb-8 text-white">{item.year}</h3>
        {item.image && (
          <div className="relative w-full mb-8">
            <img 
              src={item.image} 
              alt="" 
              className="w-full h-96 object-cover rounded-lg"
            />
          </div>
        )}
        <p className="text-xl text-white max-w-2xl">
          {item.text}
          {item.link && (
            <a
              href={item.link}
              target="_blank"
              rel="noopener noreferrer"
              className="ml-1 text-blue-400 hover:text-blue-300"
            >
              {item.linkText}
            </a>
          )}
        </p>
      </div>
    </div>
  );

  return (
    <div className="relative py-16 bg-gray-900">
      {title && (
        <div className="text-center mb-16">
          <h2 className="text-5xl font-bold mb-4 text-white">{title}</h2>
          {subtitle && (
            <p className="text-xl text-gray-300 max-w-3xl mx-auto">{subtitle}</p>
          )}
        </div>
      )}

      {showControls && (
        <>
          <button 
            onClick={() => scroll('left')}
            disabled={!canScrollLeft}
            className="absolute left-8 top-1/2 -translate-y-1/2 z-10 p-4 rounded-full bg-white/10 hover:bg-white/20 backdrop-blur-sm disabled:opacity-30 disabled:cursor-not-allowed transition-all"
            aria-label="Scroll left"
          >
            <ChevronLeft className="w-10 h-10 text-white" />
          </button>

          <button 
            onClick={() => scroll('right')}
            disabled={!canScrollRight}
            className="absolute right-8 top-1/2 -translate-y-1/2 z-10 p-4 rounded-full bg-white/10 hover:bg-white/20 backdrop-blur-sm disabled:opacity-30 disabled:cursor-not-allowed transition-all"
            aria-label="Scroll right"
          >
            <ChevronRight className="w-10 h-10 text-white" />
          </button>
        </>
      )}

      <div 
        ref={scrollContainerRef}
        className="flex overflow-x-auto gap-8 scroll-smooth scrollbar-hide snap-x snap-mandatory px-8"
      >
        {items.map((item, index) => (
          <div key={index} className="snap-start">
            {renderItem ? renderItem(item, index) : defaultRenderItem(item, index)}
          </div>
        ))}
      </div>
    </div>
  );
};

const TimelineContent = () => {
  const timelineData = [
    {
      year: "2020 (14 Years Old)",
      image: "assets/shelf.jpg",
      text: "The year of quarantine was the year of me diving into 3d printing. Having stumbled upon a 3d printed boat on TikTok I HAD to learn more. On July 12th I built my Ender 3 Pro. Since then, I've created an entire shelf worth of prints.",
      link: "https://www.3dbenchy.com",
      linkText: "3d printed boat"
    },
    {
      year: "2021-2023 (15-16)",
      image: "assets/turtleburg.jpg",
      text: "I was tasked to spend 25 hours on a project for school and I thought it would be perfect to make a game. Over 500 hours of graphic designing and coding for the first time later: Turtleburg! My edutainment RPG about the water crisis."
    },
    {
      year: "2023 (17 Years Old)",
      image: "assets/kwk.png",
      text: "After Turtleburg, I wanted to dive into other parts of CS. So, I signed up for Kode With Klossy for the summer. I didn't expect to like web development much but ended up coming out with three languages and EdVisor.",
      link: "edvisor.cruzcodes.com",
      linkText: "EdVisor"
    },
    {
      year: "2023 (17 Years Old)", 
      image: "assets/cruzcodes.jpg",
      text: "Having finished KWK, I was inspired to do web development on my own. I wanted to start simple, my own website. That in which you are scrolling through right now, Thank You!",
      link: "cruzcodes.com",
      linkText: "my own website"
    },
    {
      year: "2023 (17 Years Old)",
      image: "assets/cheezy.jpg", 
      text: "I wanted to diversify my summer and did the Girls Who Code SIP for game development. Here I made Cheezy Conundrum and learned alot about p5.js and JavaScript for game development.",
      link: "http://cheezy.cruzcodes.com",
      linkText: "Cheezy Conundrum"
    },
    {
      year: "2023 (17 Years Old)",
      image: "assets/gems.PNG",
      text: "I was inspired by my female in STEM summer that I founded a Girls STEM club at my school. Here I take on the role of teaching coding for our projects. The first project having been the website itself.",
      link: "http://dbhsgems.com",
      linkText: "website"
    },
  ];

  return <Slider items={timelineData} />;
};

export default TimelineContent;

const ToolsContent = () => {
  const toolsData = [
    { icon: "fa-brands fa-html5", name: "HTML5" },
    { icon: "fa-brands fa-css3-alt", name: "CSS3" },
    { icon: "fa-brands fa-square-js", name: "JavaScript" },
    { icon: "fa-brands fa-react", name: "React" },
    { icon: "fa-brands fa-java", name: "Java" },
    { icon: "", name: "p5.js", image: "assets/p5js.png" },
    { icon: "fa-brands fa-python", name: "Python" },
    { icon: "", name: "Godot Engine", image: "assets/godot.png" },
    { icon: "fa-brands fa-square-git", name: "Git" }
  ];

  const renderToolItem = (item) => (
    <div className="flex-none w-40 p-4">
      <div className="flex flex-col items-center justify-center">
        {item.icon ? (
          <i className={`${item.icon} text-5xl mb-3`} />
        ) : (
          <img src={item.image} alt={item.name} className="w-16 h-16 mb-3 object-contain" />
        )}
        <span className="text-lg font-medium text-center">{item.name}</span>
      </div>
    </div>
  );

  return (
    <Slider
      items={toolsData}
      renderItem={renderToolItem}
      title="Skills"
      subtitle="I love to make things both for the general web and for edutainment purposes. Here are the tools I use:"
    />
  );
};

export { Slider, TimelineContent, ToolsContent };
