import React, { useRef, useState } from 'react';

const About = () => {
  const carouselRef = useRef(null);
  const [isDragging, setIsDragging] = useState(false);
  const [startX, setStartX] = useState(0);
  const [scrollLeft, setScrollLeft] = useState(0);

  const blogPosts = [
    {
      title: "2020: My Introduction to 3D Printing",
      imgSrc: "../assets/shelf.jpg",
      description:
        "During quarantine, I discovered the world of 3D printing after stumbling upon a 3D-printed boat online. By July, I had built my Ender 3 Pro printer and began creating models, learning the mechanics of 3D printing, CAD software, and troubleshooting hardware issues. This sparked my passion for building and problem-solving.",
      link: "https://www.3dbenchy.com",
      linkText: "3D Benchy",
    },
    {
      title: "2021-2023: Developing Turtleburg",
      imgSrc: "../assets/turtleburg.jpg",
      description:
        "For a school project, I set out to create a game from scratch. Over 500 hours of coding and graphic design later, I developed Turtleburg, an edutainment RPG addressing the water crisis. This experience taught me the fundamentals of game development, from designing gameplay mechanics to implementing them in code.",
    },
    {
      title: "Summer 2023: Exploring Web Development with Kode With Klossy",
      imgSrc: "../assets/kwk.png",
      description:
        "Kode With Klossy introduced me to HTML, CSS, and JavaScript, where I discovered my interest in web development. By the end of the program, I developed EdVisor, a web app aimed at helping students manage their educational goals. This experience expanded my technical skill set and solidified my love for building user-centered applications.",
      link: "http://edvisor.cruzcodes.com",
      linkText: "EdVisor",
    },
    {
      title: "Summer 2023: Game Development with Girls Who Code SIP",
      imgSrc: "../assets/cheezy.jpg",
      description:
        "Through Girls Who Code SIP, I explored game development using p5.js and JavaScript. My project, Cheezy Conundrum, challenged me to create engaging gameplay and polish visuals. This program deepened my understanding of programming logic and creative design.",
      link: "https://cruzcodes.itch.io/cheezy-conundrum",
      linkText: "Cheezy Conundrum",
    },
    {
      title: "2023: Founding Girls STEM Club",
      imgSrc: "../assets/gems.PNG",
      description:
        "Inspired by my experiences in STEM programs, I founded a Girls STEM Club at my high school. I took on the role of teaching coding basics and leading the creation of projects like our club website. This leadership role strengthened my ability to communicate technical concepts effectively.",
      link: "http://dbhsgems.com",
      linkText: "Girls STEM Club Website",
    },
    {
      title: "Fall 2023: Building My Personal Website",
      imgSrc: "../assets/cruzcodes.jpg",
      description:
        "To solidify my web development skills, I built and deployed my personal website. This project helped me understand hosting, responsive design, and creating an online portfolio to showcase my work.",
      link: "http://cruzcodes.com",
      linkText: "My Website",
    },
    {
      title: "Fall 2024: Full-Stack Development at Cornell",
      imgSrc: "../assets/info1998.jpg",
      description:
        "In my first tech class at Cornell, INFO 1998, I developed a full-stack web application using the FERN stack (Fastify, Express, React, Node.js). This experience gave me hands-on exposure to backend development, API integration, and database design.",
    },
    {
      title: "To Be Determined: Looking Ahead",
      imgSrc: "../assets/question.jpg",
      description:
        "This is only the beginning of my journey in computer science. My next steps include building more scalable web applications, contributing to open-source projects, and mastering new frameworks to tackle real-world problems.",
    },
  ];


  const handleMouseDown = (e) => {
    setIsDragging(true);
    setStartX(e.pageX - carouselRef.current.offsetLeft);
    setScrollLeft(carouselRef.current.scrollLeft);
  };

  const handleTouchStart = (e) => {
    setIsDragging(true);
    setStartX(e.touches[0].pageX - carouselRef.current.offsetLeft);
    setScrollLeft(carouselRef.current.scrollLeft);
  };

  const handleMouseUp = () => {
    setIsDragging(false);
  };

  const handleMouseMove = (e) => {
    if (!isDragging) return;
    e.preventDefault();
    const x = e.pageX - carouselRef.current.offsetLeft;
    const walk = (x - startX) * 2;
    carouselRef.current.scrollLeft = scrollLeft - walk;
  };

  const handleTouchMove = (e) => {
    if (!isDragging) return;
    const x = e.touches[0].pageX - carouselRef.current.offsetLeft;
    const walk = (x - startX) * 2;
    carouselRef.current.scrollLeft = scrollLeft - walk;
  };

  return (
    <div className="about-container">
      <div 
        ref={carouselRef}
        className="about_slider_area"
        onMouseDown={handleMouseDown}
        onMouseUp={handleMouseUp}
        onMouseLeave={handleMouseUp}
        onMouseMove={handleMouseMove}
        onTouchStart={handleTouchStart}
        onTouchEnd={handleMouseUp}
        onTouchMove={handleTouchMove}
      >
        <div className="slider-track">
          {blogPosts.map((post, index) => (
            <div className="box-area" key={index}>
              <div className="single-blog wow fadeInUp">
                <div className="single_blog">
                  <a href="#">
                    <h3 className="post-title">{post.title}</h3>
                  </a>
                  <div className="post-img">
                    <img src={post.imgSrc} alt={post.title} />
                  </div>
                  <p className="blog-text">
                    {post.description}{" "}
                    {post.link && (
                      <a href={post.link} target="_blank" rel="noopener noreferrer">
                        {post.linkText}
                      </a>
                    )}
                  </p>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
      <style jsx>{`
  .about-container {
    max-width: 1200px;
    margin: 0 auto;
    padding: 0 1rem;
  }

  .about_slider_area {
    overflow-x: auto;
    cursor: grab;
    position: relative;
    padding: 1rem 0;
    scrollbar-width: thin;
    scrollbar-color: #888 #f1f1f1;
  }

  .about_slider_area::-webkit-scrollbar {
    height: 0.5vh;
  }

  .about_slider_area::-webkit-scrollbar-track {
    background: #f1f1f1;
    border-radius: 4px;
  }

  .about_slider_area::-webkit-scrollbar-thumb {
    background: #888;
    border-radius: 4px;
  }

  .slider-track {
    display: grid;
    grid-auto-flow: column;
    grid-auto-columns: calc(45% - 0.5rem);
    gap: 1.7rem;
    padding: 0 1rem;
  }

  /* Media queries to adjust the gap based on screen width */
  @media (max-width: 1200px) {
    .slider-track {
      gap: 4rem; 
    }
  }

  @media (max-width: 992px) {
    .slider-track {
      gap: 9rem; 
    }
  }

  @media (max-width: 768px) {
    .slider-track {
      gap: 8rem; 
    }
  }

  @media (max-width: 576px) {
    .slider-track {
      gap: 11rem; 
    }
  }
`}</style>

    </div>
  );
};

export default About;
